package week1.day1;

public class JavaOperaters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("AM:" + 250 +205);
		System.out.println("A:  " + (250+205));
		System.out.println("C : "  + (250 < 205));
		System.out.println("L : "  + ((250 < 205) && (250 < 205)));
		
		int i = 100;
		System.out.println(i++);
		System.out.println("U:" + i);

	}

}
