package week1.day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10;
		System.out.print("\nOdd Numers that are less then 10 is :");
		for (i=0; i<10; i++)
		{
			if (i%2 !=0)
			System.out.print("\r" + i);
		}
	}

}
